mapper.select:
SELECT
CASE COUNT(1)
WHEN 0 THEN FALSE ELSE TRUE END
FROM
kb_workspace kw
WHERE kw.parent_id = #{parentId}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null


AND kw.is_delete = 0

SELECT
MAX(kw.rank)
FROM
kb_workspace kw
WHERE kw.parent_id = #{parentId}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null

SELECT
MIN(kw.rank)
FROM
kb_workspace kw
WHERE kw.parent_id = #{parentId}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null

SELECT
kw.RANK
FROM
kb_workspace kw
WHERE kw.ID = #{id}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null

SELECT
MAX(kw.rank)
FROM
kb_workspace kw
WHERE kw.parent_id = #{parentId}
AND kw.rank < #{rightRank}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null

SELECT
MIN(kw.rank)
FROM
kb_workspace kw
WHERE kw.parent_id = #{parentId}
AND kw.rank > #{leftRank}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null

SELECT kw.* FROM kb_workspace kw WHERE
kw.parent_id IN
#{parentId}
AND kw.organization_id = #{organizationId}
AND kw.project_id = #{projectId}
AND kw.project_id is null
kw.parent_id = -1

mapper.update:
UPDATE kb_workspace
SET ROUTE = REPLACE(ROUTE,#{oldRoute},#{newRoute})
WHERE ROUTE REGEXP '^${oldRoute}.'

    AND organization_id = #{organizationId}



        AND project_id = #{projectId}


        AND project_id is null

UPDATE kb_workspace
SET is_delete = #{delete}
WHERE ROUTE REGEXP '^${route}.'

    AND organization_id = #{organizationId}



        AND project_id = #{projectId}


        AND project_id is null

mapper.select:
SELECT * FROM kb_workspace kw WHERE 1 = 1
AND kw.organization_id = #{organizationId}
AND kw.project_id = #{projectId}
AND kw.project_id is null
AND kw.is_delete = 0 ORDER BY kw.RANK ASC

SELECT * FROM kb_workspace kw WHERE 1 = 1
AND kw.organization_id = #{organizationId}
AND kw.project_id = #{projectId}
AND kw.project_id is null
AND kw.is_delete = 1 ORDER BY kw.last_update_date DESC, kw.rank ASC

select kw.*
from kb_workspace kw
where project_id = #{projectId}
and kw.id in

    #{spaceId}
