mapper.select:
SELECT CASE COUNT(1) WHEN 0 THEN FALSE ELSE TRUE END FROM kb_workspace kw WHERE kw.parent_id = #{parentId} AND
kw.organization_id = #{resourceId}
kw.project_id = #{resourceId}

SELECT
MAX(kw.rank)
FROM
kb_workspace kw
WHERE
kw.parent_id = #{parentId}
AND

    kw.organization_id = #{resourceId}


    kw.project_id = #{resourceId}

SELECT
MIN(kw.rank)
FROM
kb_workspace kw
WHERE
kw.parent_id = #{parentId}
AND

    kw.organization_id = #{resourceId}


    kw.project_id = #{resourceId}

SELECT
kw.RANK
FROM
kb_workspace kw
WHERE
kw.ID = #{id}
AND

    kw.organization_id = #{resourceId}


    kw.project_id = #{resourceId}

SELECT
MAX(kw.rank)
FROM
kb_workspace kw
WHERE
kw.parent_id = #{parentId}
AND kw.rank < #{rightRank}
AND

    kw.organization_id = #{resourceId}


    kw.project_id = #{resourceId}

SELECT
MIN(kw.rank)
FROM
kb_workspace kw
WHERE
kw.parent_id = #{parentId}
AND kw.rank > #{leftRank}
AND

    kw.organization_id = #{resourceId}


    kw.project_id = #{resourceId}

mapper.delete:
DELETE
FROM kb_workspace
where ROUTE REGEXP "^${route}";

mapper.select:
SELECT kw.* FROM kb_workspace kw WHERE
kw.parent_id IN
#{parentId}
AND
kw.organization_id = #{resourceId}
kw.project_id = #{resourceId}
ORDER BY kw.RANK ASC
kw.parent_id = -1

SELECT
kw.*
FROM
kb_workspace kw
WHERE
kw.parent_id = #{parentId}
AND

    kw.organization_id = #{resourceId}


    kw.project_id = #{resourceId}

ORDER BY kw.RANK ASC

mapper.update:
UPDATE kb_workspace SET ROUTE = REPLACE(ROUTE,#{odlRoute},#{newRoute}) WHERE ROUTE REGEXP "^${odlRoute}" AND
organization_id = #{resourceId}
project_id = #{resourceId}
